mapper.select:
SELECT NVL(COUNT(*), 0) FROM freebbs
userId = #{userId}
AND

SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT a.freenum, userId, subject, imageFilename
		FROM freebbs a
		LEFT OUTER JOIN (
			SELECT freeFileNum, freeNum, saveFilename FROM (
				SELECT freeFileNum, freeNum, imageFilename, 
					ROW_NUMBER() OVER(PARTITION BY freeNum ORDER BY fileNum ASC) rank
				 FROM freebbsFile
			) WHERE rank = 1
		) i ON a.freeNum = i.freeNum
		
			userId = #{userId}
			
				AND 
			
		
		ORDER BY num DESC			
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
