mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(PARTITION BY h.hotelNum ORDER BY h.hotelNum ASC) rank, h.hotelNum, p.partnerTel, hotelName, hotelIntro, hotelNumber, hotelZip, hotelAddr1, hotelAddr2, imageFileNum hotelImageFileNum, saveFilename hotelSaveFilename FROM hotel h JOIN partner p ON p.partnerNum = h.partnerNum JOIN roomInfo r ON h.hotelNum = r.hotelNum LEFT JOIN hotelImage hI ON h.hotelNum = hI.hotelNum
INSTR(roomOption, #{item}) >= 1
) WHERE rank=1

SELECT imageFileNum, hotelNum, saveFilename
FROM hotelImage
WHERE hotelNum = #{hotelNum}

	SELECT imageFileNum, hotelNum, saveFilename
	FROM hotelImage
    WHERE imageFileNum = #{imageFileNum}

SELECT userId, userName, email, tel
FROM member
WHERE userId = #{userId}
